import React, { Component } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import Spinner from '../layout/Spinner'

class Details extends Component {
  state ={
   details:{}
  }

  componentDidMount(){
    axios.get(`https://www.rijksmuseum.nl/api/en/collection/${this.props.match.params.id}?q=Q&key=${process.env.REACT_APP_MM_KEY}&imgonly=true&format=json`)
    .then(res => {
      console.log(res.data.artObject);
      this.setState({details: res.data.artObject})
    })
    .catch(err => console.log(err));
  }

  render() {
    const { details } = this.state;
  
    if(details === undefined || Object.keys(details).length === 0 ){
      return <Spinner />
    }else{
      return (
        <React.Fragment>
          <Link to="/" className="btn btn-warning btn-sm mb-4">Go Back</Link>
          <div className="card mb-3" style={{maxWidth:'540px'}}>
            <div className="row no-gutters">
              {/* <div className="col-md-4">
                <img src={details.webImage.url} className="card-img" alt="">
              </div> */}
              <div className="col-md-8">
                 <div className="card-body">
                    <h5 className="card-title">Card title</h5>
                     <p className="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                     <p className="card-text"><small className="text-muted">Last updated 3 mins ago</small></p>
                  </div>
              </div>
  </div>
</div>
        </React.Fragment>
      );
    }
  }
}

export default Details
